import edu.duke.FileResource;
import edu.duke.StorageResource;

class Part1 {

  public int findStopCodon(String dna, int startIndex, String stopCodon) {
    int currIndex = dna.indexOf(stopCodon, startIndex + 3);
    while (currIndex != -1) {
      int diff = currIndex - startIndex;
      if (diff % 3 == 0) {
        return currIndex;
      } else {
        currIndex = dna.indexOf(stopCodon, currIndex + 1);
      }
    }
    return -1;
  }

  public void testFindStopCodon() {
    FileResource fr = new FileResource("brca1line.fa");
    String dna = fr.asString().toUpperCase();
    System.out.println(findStopCodon(dna, 0, "TAG"));
  }

  public String findGene(String dna, int where) {
    int startIndex = dna.indexOf("ATG", where);
    if (startIndex == -1) {
      return "";
    }
    int taaIndex = findStopCodon(dna, startIndex, "TAA");
    int tagIndex = findStopCodon(dna, startIndex, "TAG");
    int tgaIndex = findStopCodon(dna, startIndex, "TGA");
    int minIndex = 0;
    if (taaIndex == -1 || (tgaIndex != -1 && tgaIndex < taaIndex)) {
      minIndex = tgaIndex;
    } else {
      minIndex = taaIndex;
    }
    if (minIndex == -1 || (tagIndex != -1 && tagIndex < minIndex)) {
      minIndex = tagIndex;
    }
    if (minIndex == -1) {
      return "";
    }
    return dna.substring(startIndex, minIndex + 3);
  }

  public void testFindGene() {
    FileResource fr = new FileResource("brca1line.fa");
    String dna =
      "".toUpperCase();
    System.out.println(findGene(dna, 0));
  }

  public StorageResource getAllGenes(String dna) {
    StorageResource geneList = new StorageResource();
    int startIndex = 0;
    while (true) {
      String currentGene = findGene(dna, startIndex);
      System.out.println(currentGene);
      if (currentGene.isEmpty()) {
        break;
      }
      geneList.add(currentGene);
      startIndex = dna.indexOf(currentGene, startIndex) + currentGene.length();
    }
    return geneList;
  }

  public void testGetGenes() {
    FileResource fr = new FileResource("brca1line.fa");
    String dna = fr.asString().toUpperCase();
    int count = 0;
    dna =
      "";
    System.out.println("Getting all genes on " + dna);

    StorageResource genes = getAllGenes(dna);
    for (String g : genes.data()) {
      System.out.println(g);
      count++;
    }
    System.out.println("Number of genes: " + count);
  }
}

class Part2 {

  public float cgRatio(String dna) {
    int cg = 0;
    int startIndex = 0;
    while (startIndex < dna.length()) {
      if (dna.indexOf("C", startIndex) != -1) {
        startIndex = dna.indexOf("C", startIndex);
        cg++;
      } else if (dna.indexOf("G", startIndex) != -1) {
        startIndex = dna.indexOf("G", startIndex);
        cg++;
      } else {
        startIndex++;
      }
    }
    return (float) ((float) cg / (float) (dna.length()));
  }

  public void cgTest() {
    String dna = "CGCGCAAAAAAAAAGCGCG";
    System.out.println(cgRatio(dna));
  }

  public int countCTG(String dna) {
    int count = 0;
    int startIndex = 0;
    while (startIndex < dna.length()) {
      if (dna.indexOf("CTG", startIndex) != -1) {
        count++;
        startIndex = dna.indexOf("CTG", startIndex) + 3;
      } else {
        startIndex++;
      }
    }
    return count;
  }

  public void testCTG() {
    FileResource fr = new FileResource("GRch38dnapart.fa");
    String dna = fr.asString().toUpperCase();
    System.out.println(countCTG(dna));
  }
}

class Part3 {

  public void processGenes(StorageResource sr) {
    StorageResource longerThanNineArray = new StorageResource();
    StorageResource cgRatioArray = new StorageResource();
    String longestGene = "";
    Part2 cgRatio = new Part2();

    for (String d : sr.data()) {
      if (d.length() > 60) {
        longerThanNineArray.add(d);
      }
      if (cgRatio.cgRatio(d) > 0.35) {
        cgRatioArray.add(d);
      }
      if (d.length() > longestGene.length()) {
        longestGene = d;
      }
    }
    System.out.println(
      "Length bigger than 60(" + longerThanNineArray.size() + "): "
    );
    for (String d : longerThanNineArray.data()) {
      System.out.println(d);
    }
    System.out.println(
      "CG ratio bigger than 0.35(" + cgRatioArray.size() + "): "
    );
    for (String d : cgRatioArray.data()) {
      System.out.println(d);
    }
    System.out.println("Longest gene: " + longestGene.length());
  }

  public void testProcessGenes() {
    FileResource fr = new FileResource("GRch38dnapart.fa");
    String dna = fr.asString().toUpperCase();
    Part1 getGenes = new Part1();
    StorageResource genes = getGenes.getAllGenes(dna);
    processGenes(genes);
  }
}

